#!/bin/bash

# -----------------------------------------------------
# 	Build Script for Kali as of 2021.4 (Chris M)
# -----------------------------------------------------

# (TODO) make this script work for other than the 'kali' user

# Check UID = 0
if [ "$(id -u)" -ne 0 ]; then
   echo "This script must be run as root" 1>&2
   exit 1
fi

# Check Disk Usage on each partition (assume /root and /home/kali have space)
if [  $(df --output=avail -h / | sed '1d;s/[^0-9]//g') -lt 20 ]; then
	echo "You must have at least 20G of space remaining on /"
	exit 2
fi
if [  $(df --output=avail -h /opt | sed '1d;s/[^0-9]//g') -lt 4 ]; then
	echo "You must have at least 4G of space remaining on /opt and /var/www"
	exit 2
fi
if [  $(df --output=avail -h /var/www | sed '1d;s/[^0-9]//g') -lt 2 ]; then
	echo "You must have at least 2G of space remaining on /var/www"
	exit 2
fi

# Check network connectivity
ping github.com -c1 -w1 | grep ttl > /dev/null;
if [[ $? -ne 0 ]]; then
	echo "Could not connect to github. This script needs good internet to run"
	exit 3
fi


# Update this as APT and PIP packages need updating
PACKAGES = "sublime-text tilix xclip ferroxbuster zaproxy jd-gui ghex zeek seclists bloodhound neo4j dsniff brutespray flameshot hashcat chisel freerdp2-x11 python3-impacket impacket-scripts python3-pcapy python3-qpid-proton xsel adb atftp cmake dbeaver shellter sherlock rinetd gedit ghidra gobuster httrack sshuttle knockd ipmitool wine mingw-w64 ncat ntpdate ntp perl php pure-ftpd exiftool sshpass sublist3r smtp-user-enum edb-debugger libmariadb-dev gcc-multilib crowbar gobuster nishang default-libmysqlclient-dev steghide starkiller twofi wine64 wine snmpenum maltego mlocate build-essential git libdistorm3-dev yara libraw1394-11 libcapstone-dev capstone-tool tzdata python3 python3-dev libpython3-dev python3-pip python3-setuptools python3-wheel python2 python2.7-dev libpython2-dev cherrytree fcrackzip ghidra python3-poetry maven lshw net-tools ipmiutil wireless-tools snapd golang bettercap strace ltrace linux-exploit-suggester vlc gconf2 npm"
PYTHONPACKAGES = "distorm3 yara pycrypto pillow openpyxl ujson pytz ipython capstone pysmbclient pycryptodome pcapy scapy oletools mechanize paramiko dnspython pyftpdlib pyftpdlib pwntools pwn bs4"

# Set sudo permissions for kali user
echo 'kali ALL=(ALL:ALL) NOPASSWD: ALL' > /etc/sudoers.d/kali
chmod 440 /etc/sudoers.d/kali

# Install dependencies for sublime text
cd /opt
wget -qO - https://download.sublimetext.com/sublimehq-pub.gpg | apt-key add -
apt install apt-transport-https
echo "deb https://download.sublimetext.com/ apt/stable/" | tee /etc/apt/sources.list.d/sublime-text.list
apt -y update && apt -y upgrade

# Install PACKAGES from Apt
for i in PACKAGES; do apt install -y $i; done

# Install PYTHONPACKAGES from pip3
for i in PYTHONPACKAGES; do python3 -m pip install -U $i; done

# Fix some installs
download-mibs
ln -s /opt/sublime_text/sublime_text /usr/bin/sublime-text
mv /usr/share/linux-exploit-suggester/linux-exploit-suggester.sh /opt/linux-exploit-suggester.sh
rmdir /usr/share/linux-exploit-suggester

# Fix Gitbook (for hacktricks)
npm install -g gitbook-cli
npm install graceful-fs@latest --save
sed -i '62 i /*' /usr/local/lib/node_modules/gitbook-cli/node_modules/npm/node_modules/graceful-fs/polyfills.js
sed -i '66 i /*' /usr/local/lib/node_modules/gitbook-cli/node_modules/npm/node_modules/graceful-fs/polyfills.js

# VSCode for Matt 
apt purge code-oss
wget 'https://code.visualstudio.com/sha/download?build=stable&os=linux-deb-x64' -O /opt/vscode.deb
dpkg -i /opt/vscode.deb && rm -f /opt/vscode.deb

# Fix and update issues
apt -y autoremove
apt -y update && apt -y upgrade
updatedb
apt -y dist-upgrade

# add Tilix color scheme
mkdir -p /root/.config/tilix/schemes
mkdir -p /home/kali/.config/tilix/schemes
