cp /root/.config/tilix/schemes/Tilix_dark.json /home/kali/.config/tilix/schemes/Tilix_dark.json

#Setup PowerLevel10k for Tilix and .zshrc
mkdir /root/.fonts
cd /root/.fonts
wget 'https://github.com/romkatv/powerlevel10k-media/raw/master/MesloLGS%20NF%20Regular.ttf'
wget 'https://github.com/romkatv/powerlevel10k-media/raw/master/MesloLGS%20NF%20Bold.ttf'
wget 'https://github.com/romkatv/powerlevel10k-media/raw/master/MesloLGS%20NF%20Italic.ttf'
wget 'https://github.com/romkatv/powerlevel10k-media/raw/master/MesloLGS%20NF%20Bold%20Italic.ttf'
cp -r /root/.fonts /home/kali/.fonts
cd /opt 
git clone --depth=1 https://github.com/romkatv/powerlevel10k.git /root/powerlevel10k
cp -r /root/powerlevel10k /home/kali/powerlevel10k
echo '' > /root/.zshrc
cp /root/.zshrc /home/kali/.zshrc
echo 'source /root/powerlevel10k/powerlevel10k.zsh-theme' >>/root/.zshrc
echo 'source /home/kali/powerlevel10k/powerlevel10k.zsh-theme' >>/home/kali/.zshrc
chown -R kali:kali /home/kali/

# Setup sublime text theme
sublime-text
for i in $(ps aux | grep sublime | grep root | grep -v grep | awk '{print $2}'); do kill -9 $i; done 
mkdir -p /root/.config/sublime-text/Packages/User/
echo "" | base64 -d > /root/.config/sublime-text/Packages/User/Adaptive.sublime-theme
echo "" | base64 -d > /root/.config/sublime-text/Packages/User/Monokai.sublime-color-scheme
cp /root/.config/sublime-text/Packages/User/Adaptive.sublime-theme /home/kali/.config/sublime-text/Packages/User/Adaptive.sublime-theme
cp /root/.config/sublime-text/Packages/User/Monokai.sublime-color-scheme /home/kali/.config/sublime-text/Packages/User/Monokai.sublime-color-scheme

#fix permissions in .config
chown -R kali:kali /home/kali/.config

# Recompile Evil-WinRM
