git clone https://github.com/Hackplayers/evil-winrm
cd /opt/evil-winrm
gem uninstall evil-winrm -q
gem uninstall evil-winrm -q
apt install -f rbenv
echo 'export PATH="$HOME/.rbenv/bin:$PATH"' >> ~/.zshrc
echo 'eval "$(rbenv init -)"' >> ~/.zshrc
source ~/.zshrc
export RUBY_CONFIGURE_OPTS=--with-readline-dir="/usr/include/readline"
rbenv install 2.7.1
rbenv local 2.7.1
gem install bundler
bundle install
current_evwr="$(pwd)/evil-winrm.rb"
sudo bash -c "cat << 'EOF' > /usr/bin/evil-winrm
#!/usr/bin/env sh
/opt/evil-winrm/evil-winrm.rb \$@
EOF"
chmod 755 /usr/bin/evil-winrm


